mapper.select:
SELECT mk.id, mm.name AS material_name, mk.order_number, mk.factory_code , mk.ware_code, mk.area_code, mk.bin_code, mk.material_code, mk.quantity, mk.move_type, mk.sscc_number, mk.cell, mk.type, mk.status, mk.create_by, mk.update_by, mk.create_time, mk.update_time FROM material_kanban mk LEFT JOIN (SELECT * from md_material where delete_flag='0' )mm ON mk.material_code=mm.code WHERE 1=1 AND mk.delete_flag='0' AND (mk.status <> 7 and mk.status <> 6)
AND mk.ware_code=#{wareCode}
AND mk.cell=#{cell}

SELECT mk.id, mm.name AS material_name, mk.order_number, mk.factory_code, mk.ware_code, mk.area_code, mk.bin_code, mk.material_code, mk.quantity, mk.move_type, mk.sscc_number, mk.cell, mk.type, mk.status, mk.create_by, mk.update_by, mk.create_time, mk.update_time FROM material_kanban mk LEFT JOIN (SELECT * from md_material where delete_flag='0' )mm ON mk.material_code=mm.code WHERE 1=1 AND mk.delete_flag='0'
AND mk.ware_code=#{wareCode}
AND mk.cell=#{cell}
AND (mk.status = #{status})

SELECT bs.batch_nb, mm.name AS material_name, mk.id, mk.order_number, bs.plant_nb as factory_code, bs.ware_code, bs.area_code, bs.bin_code, mk.material_code, mk.quantity, mk.move_type, mk.sscc_number, mk.cell, mk.type, mk.status, mk.create_by, mk.update_by, mk.create_time, mk.update_time FROM material_kanban mk LEFT JOIN (SELECT * FROM bi_stock )bs ON mk.sscc_number = bs.sscc_number LEFT JOIN (SELECT * from md_material where delete_flag='0' )mm ON mk.material_code=mm.code WHERE 1=1 AND mk.delete_flag='0'
AND bs.ware_code=#{wareCode}
AND mk.sscc_number=#{sscc}
limit 1

SELECT mk.id, mm.name AS material_name, mk.order_number, mk.factory_code , mk.ware_code, mk.area_code, mk.bin_code, mk.material_code, mk.quantity, mk.move_type, mk.sscc_number, mk.cell, mk.type, mk.status, mk.create_by, mk.update_by, mk.create_time, mk.update_time FROM material_kanban mk LEFT JOIN (SELECT * from md_material where delete_flag='0' )mm ON mk.material_code=mm.code WHERE 1=1 AND mk.delete_flag='0'
and mk.order_number like concat('%', #{orderNumber}, '%')
and mk.factory_code like concat('%', #{factoryCode}, '%')
and mk.ware_code like concat('%', #{wareCode}, '%')
and mk.area_code like concat('%', #{areaCode}, '%')
and mk.material_code like concat('%', #{materialCode}, '%')
and mk.sscc_number like concat('%', #{ssccNumber}, '%')
and mk.bin_code like concat('%', #{binCode}, '%')
and mk.cell like concat('%', #{cell}, '%')
and mk.create_by like concat('%', #{createBy}, '%')
and mk.update_by like concat('%', #{updateBy}, '%')
AND mk.type=#{type}
AND mk.status=#{status}
AND mk.create_time >= #{createTimeStart}
AND mk.create_time <= #{createTimeEnd}
AND mk.update_time >= #{updateTimeStart}
AND mk.update_time <= #{updateTimeEnd}

SELECT bs.batch_nb, mm.name AS material_name, mk.id, mk.order_number, bs.plant_nb as factory_code, bs.ware_code, bs.area_code, bs.bin_code, mk.material_code, mk.quantity, mk.move_type, mk.sscc_number, mk.cell, mk.type, mk.status, mk.create_by, mk.update_by, mk.create_time, mk.update_time FROM material_kanban mk LEFT JOIN (SELECT * FROM bi_stock )bs ON mk.sscc_number = bs.sscc_number LEFT JOIN (SELECT * from md_material where delete_flag='0' )mm ON mk.material_code=mm.code WHERE 1=1 AND mk.delete_flag='0'
AND mk.sscc_number=#{sscc}
limit 1
