mapper.select:
select bs.id as id, bs.sscc_number as sscc_number, bs.plant_nb as plant_nb, bs.from_purchase_order as from_purchase_order, bs.ware_code as ware_code, bs.frame_code as frame_code, bs.bin_code as bin_code, bs.material_nb as material_nb, mm.name as material_name, bs.batch_nb as batch_nb, bs.expire_date as expire_date, bs.total_stock as total_stock, bs.freeze_stock as freeze_stock, bs.available_stock as available_stock, bs.bin_in_id as bin_in_id, bs.create_by as create_by, bs.create_time as create_time, bs.quality_status as quality_status, bs.area_code as area_code, bs.pallet_code as pallet_code from bi_stock bs left join md_ware mw on mw.code = bs.ware_code left join md_material mm on bs.material_nb = mm.code where bs.delete_flag=0 and bs.sscc_number like concat('%', #{ssccNumber}, '%') and bs.plant_nb like concat('%', #{plantNb}, '%') and bs.from_purchase_order like concat('%', #{fromPurchaseOrder}, '%') and bs.ware_code like concat('%', #{wareCode}, '%') and bs.area_code like concat('%', #{areaCode}, '%') and bs.pallet_code like concat('%', #{palletCode}, '%') and bs.frame_code like concat('%', #{frameCode}, '%') and bs.bin_code like concat('%', #{binCode}, '%') and bs.material_nb like concat('%', #{materialNb}, '%') and bs.batch_nb like concat('%', #{batchNb}, '%') and bs.create_by like concat('%', #{operateUser}, '%')
and str_to_date(bs.expire_date, '%d/%m/%y') between #{startExpireDate} and #{endExpireDate}
and bs.quality_status = #{qualityStatus}

select bs.id as id, bs.sscc_number as sscc_number, bs.plant_nb as plant_nb, bs.from_purchase_order as from_purchase_order, bs.ware_code as ware_code, bs.frame_code as frame_code, bs.bin_code as bin_code, bs.material_nb as material_nb, mm.name as material_name, bs.batch_nb as batch_nb, bs.expire_date as expire_date, bs.total_stock as total_stock, bs.freeze_stock as freeze_stock, bs.available_stock as available_stock, bs.bin_in_id as bin_in_id, bs.create_by as create_by, bs.create_time as create_time, bs.quality_status as quality_status from bi_stock bs left join md_ware mw on mw.code = bs.ware_code left join md_material mm on bs.material_nb = mm.code where bs.quality_status = 'U' and bs.freeze_stock =0 and bs.delete_flag=0 and bs.material_nb = #{materialNb}
and bs.ware_code = #{wareCode} and bs.batch_nb = #{batchNb}
order by bs.expire_date desc,bs.plant_nb
order by bs.plant_nb,bs.expire_date desc
