mapper.select:
select f.id as id,w.id as ware_id ,w.code as ware_code,w.name as ware_name,
       a.id as area_id, a.name as area_name,a.code as area_code,f.code as code,f.height as height,f.type_code as type_code,
       f.name as name,f.width as width,f.bear_weight as bear_weight,f.create_time as create_time
from md_frame f
    left join md_area a on f.area_id=a.id
    left join md_ware w on w.id=a.ware_id

     and f.area_id = #{areaId}
     and f.name like concat('%', #{name}, '%')
     and f.code = #{code}
     and f.width = #{width}
     and f.bear_weight = #{bearWeight}
     and f.height = #{height}
     and f.type_code = #{typeCode}

mapper.update:
update md_frame
area_id = #{areaId}, name = #{name}, code = #{code}, width = #{width}, height = #{height}, type_code = #{typeCode}, bear_weight = #{bearWeight}, create_by = #{createBy}, update_by = #{updateBy}, create_time = #{createTime}, update_time = #{updateTime}, status = #{status}, remark = #{remark},
where id = #{id}
