mapper.select:
select ws.*,
mm.product_name as material_name,
tr.order_number as order_nb,tr.car_nb as carNb
from product_ware_shift ws
left join (SELECT * from md_product_packaging where delete_flag = '0') mm on ws.material_nb = mm.product_no
left join transhipment_order tr on tr.product_ware_shift_id = ws.id and tr.delete_flag = 0 and tr.sscc_number =
ws.sscc_nb
where ws.delete_flag = 0

    AND ws.source_plant_nb=#{sourcePlantNb}


    AND ws.source_ware_code=#{sourceWareCode}


    AND tr.order_number=#{orderNb}


    AND ws.target_ware_code=#{targetWareCode}


    AND (ws.status = #{status})


    AND tr.car_Nb=#{carNb}


    AND ws.sscc_nb=#{ssccNb}

and ws.create_by like concat('%', #{createBy}, '%')
and ws.create_time  >=  #{startCreateTime}
and ws.create_time  <=  #{endCreateTime}
and ws.update_time  >=  #{startUpdateTime}
and ws.update_time  <=  #{endUpdateTime}

order by update_time desc, create_time desc
