mapper.select:
SELECT

FROM shipping_history
WHERE delete_flag=0

    AND id = #{id}


    AND shipping_task_id = #{shippingTaskId}


    AND sscc_numbers LIKE CONCAT('%', #{ssccNumbers}, '%')


    AND eto_po LIKE CONCAT('%', #{etoPo}, '%')


    AND prod_order LIKE CONCAT('%', #{prodOrder}, '%')


    AND status = #{status}


    AND create_by = #{createBy}


    AND create_time = #{createTime}


    AND update_by = #{updateBy}


    AND update_time = #{updateTime}

SELECT
,

FROM shipping_task as st left join shipping_history as sh
ON sh.shipping_task_id=st.id
WHERE st.delete_flag=0 and sh.delete_flag=0


    AND st.shipping_mark like concat('%',#{shippingMark},'%')



    AND sh.history_index = #{historyIndex}


    AND sh.shipping_task_id = #{shippingTaskId}



    AND st.eto_po like concat('%',#{etoPo},'%')


and sh.create_time  >=  #{startCreateTime}
and sh.create_time  <=  #{endCreateTime}

    AND sh.create_by = #{historyCreateBy}


    AND st.after_packing = #{afterPacking}


    AND sh.sscc_numbers like concat('%',#{ssccNumbers},'%')
