mapper.select:
select sm.id, sm.sscc_number, sm.batch_nb, str_to_date(sm.expire_date, '%d/%m/%Y') as expire_date, sm.material_nb, mm.name as material_name, sm.unit, sm.quantity, sm.from_purchase_order, sm.po_number_item, sm.upload_user, sm.upload_time, sm.update_user, sm.update_time, sm.delete_flag, sm.status, sm.edit_flag, sm.plant_nb, sm.ware_code from si_material_receive sm left join (select * from md_material where delete_flag=0 )mm on sm.material_nb = mm.code where sm.delete_flag=0 and sm.plant_nb like concat('%', #{plantNb}, '%') and sm.ware_code like concat('%', #{wareCode}, '%')
and sm.material_nb like concat('%', #{materialNb}, '%') and sm.batch_nb like concat('%', #{batchNb}, '%') and sm.sscc_number like concat('%', #{ssccNumber}, '%')
and sm.from_purchase_order like concat('%', #{fromPurchaseOrder}, '%')
and sm.po_number_item like concat('%', #{poNumberItem}, '%')
and str_to_date(sm.expire_date, '%d/%m/%Y') between #{startExpireDate} and #{endExpireDate}
and str_to_date(sm.upload_time, '%d/%m/%Y') between #{startUploadTime} and #{endUploadTime}
and sm.status = #{status} order by sm.update_time desc, sm.upload_time desc
,status ${orderType}
,from_purchase_order ${orderType}
,batch_nb ${orderType}

mapper.update:
update si_material_receive
set delete_flag = 1
where id = #{id}

update si_material_receive set delete_flag = 1
where id in

    #{id}

update si_material_receive
set status = #{status}
where sscc_number = #{ssccNumber}

update si_material_receive
set edit_flag = #{editFlag}
where id in

    #{id}

update si_material_receive
set status     = #{code},
    update_user=#{updateUser},
    update_time=#{updateTime}
where material_nb = #{materialNb}
  and batch_nb = #{batchNb}
  and delete_flag = 0
