mapper.select:
select sm.id, sm.sscc_number, sm.batch_nb, str_to_date(sm.expire_date, '%d/%m/%y') as expire_date, sm.material_nb, mm.name as material_name, sm.unit, sm.quantity, sm.from_purchase_order, sm.po_number_item, sm.upload_user, sm.upload_time, sm.update_user, sm.update_time, sm.delete_flag, sm.status, sm.edit_flag, sm.plant_nb from si_material_receive sm left join md_material mm on sm.material_nb = mm.code where sm.delete_flag=0 and sm.plant_nb = #{plantNb} and sm.material_nb = #{materialNb} and sm.batch_nb = #{batchNb} and sm.sscc_number = #{ssccNumber}
and sm.from_purchase_order = #{fromPurchaseOrder}
and sm.po_number_item = #{poNumberItem}
and str_to_date(sm.expire_date, '%d/%m/%y') between #{startExpireDate} and #{endExpireDate}
and str_to_date(sm.upload_time, '%d/%m/%y') between #{startUploadTime} and #{endUploadTime}
and sm.status = #{status}

mapper.update:
update si_material_receive
set delete_flag = 1
where id = #{id}

update si_material_receive set delete_flag = 1
where id in

    #{id}

update si_material_receive
set status = #{status}
where sscc_number = #{ssccNumber}

update si_material_receive
set edit_flag = #{editFlag}
where id in

    #{id}

update si_material_receive
set status     = #{code},
    update_user=#{updateUser},
    update_time=#{updateTime}
where material_nb = #{materialNb}
  and batch_nb = #{batchNb}
  and delete_flag = 0
